    def cadastrando(self):
        email = self.edt_email.text()
        senha = self.edt_senha.text()
        senhaConf = self.edt_senhaConf.text()

        conn = sqlite3.connect('login.db')
        cursor = conn.cursor()
        cursor.execute("SELECT email FROM cadastrados WHERE email ='{}'".format(email))
        emailDb = cursor.fetchall()
        print(emailDb)
        verificaEmail = False
        if not emailDb:
            verificaEmail = False
        else:
            verificaEmail = True
        if senha != senhaConf or email == "" or senha == "" or senhaConf == "":
            dlg = CustomDialog()
            dlg.exec()
            print("Senhas n√£o batem ou campos em branco")
        elif verificaEmail == True:
            dlg = CustomDialogEqualEmail()
            dlg.exec()

        else:
            cursor = conn.cursor()
            cursor.execute("INSERT INTO cadastrados (email, senha) VALUES ('{}', '{}')".format(email, senha))

            conn.commit()
            conn.close()
            print("Email cadastrado com sucesso")
            dlg = CustomEmailCreated()
            dlg.exec()
